import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import  './UserPage.css';
class UserPage  extends React.Component {
  constructor(props) {
   	super(props);
     	this.state = {
      		name:"Nothing",
      		surname:"",
      		email_address:"",
      		Phone_Number: "",
      		Biography: "",
      		con:false,
    	};
    	if(props.location.state == null){
    		this.state.con=false	
    	}
    	else{
    		this.state.con=props.location.state.isLogin
    		this.state.email_address=props.location.state.Email
    	}
    	this.getData = this.getData.bind(this);
    	this.handleName=this.handleName.bind(this);
  	}
  	handleName(e){
  		this.setState({name: e.target.value});
  	}
  	getData(){
  		alert("Nikos")
  		const formData = new FormData();
  		formData.append('email',this.state.email_address)
  		axios.post('http://127.0.0.1:8000/users/retrieve/', formData, {headers: {'Content-Type': 'application/json'}})
        .then(response => {
            /*alert(response.data[0])*/
            this.handleName(response.data[0])
            /*alert(this.state.name)*/
        }).catch(error => {
            alert("mpika kai edw")
        }).finally(() => { 
        })
  	}
  	render() {
  		this.getData()
  		if(this.state.con==false){
  			return (<Redirect to='/'/>);
  		}
  		else{
  			return(
  				<h1>{this.state.name}</h1>
  			);
  		}
  	}
}

export default UserPage;
