import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import Plot from "./Plot.js"
class Settings  extends React.Component {
	constructor(props){
    	super(props);
    	this.state = {
            id: -1,
      		email_address:"",
      		Password:"",
      		new_password:"",
      		new_password_again:"",
    	};
    	if(props.location.state == null){
    		this.state.con=false	
    	}
    	else{
    		this.state.con=true
    		this.state.email_address=props.location.state.Email
    		this.state.id=props.location.state.id
    	}
  	}
	render(){
		if(this.state.con === false){
  			return (<Redirect to='/'/>);
  		}
  		else{
			return(
				<div>
					<Plot name={"Settings"} id={this.state.id} email={this.state.email_address}/>
					 <h4>Change Email</h4>
					 <form onSubmit={this.handle}>
			          <label>New Email Address:</label>
			          <input type="text"  id="Email" name="Email" defaultValue={this.state.email}  onChange={this.handleEmail} size="61"/><br/>
			          <label for="Password">Password:</label>
			          <input  type="password" id="Password" name="Password" defaultValue={this.state.Password}  onChange={this.handlePassword}  size="69"/><br/><br/>
			          <button>Submit</button>
			        </form>
			        <br/>
			        <br/>
			        <br/>
			        <br/>
			        <br/>
			        <br/>
			        <br/>
			        <form>
			        <h4>Change Password</h4>
			        <label>Password:</label>
			        <input type="password"  id="password" name="password" defaultValue={this.state.email}  onChange={this.handleEmail} size="78"/><br/>
			        <label for="Password">New Password:</label>
			        <input  type="password" id="newPassword" name="newPassword" defaultValue={this.state.Password}  onChange={this.handlePassword}  size="74"/><br/>
					<label for="Password">New Password Again:</label>
			        <input  type="password" id="newPassword_again" name="newPassword_again" defaultValue={this.state.Password}  onChange={this.handlePassword}  size="69"/><br/><br/>
					<button>Submit</button>
					</form>
				</div>
			);
		}
	}
}
export default Settings;
