import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import Settings from './Settings.js'
import {Route,PrivateRoute} from 'react-router-dom';
import { BrowserRouter as Router } from 'react-router-dom';
import './Network.css'
class Network extends React.Component {
	constructor(props) {
   		super(props);
  		this.state = {
	     	email: "",
	     	id:-1,
	     	con:false,
	     	user_search:"",
	    };
	    if(props.location.state == null){
    		this.state.con=false	
    	}
    	else{
    		this.state.con=true
    		this.state.email_address=props.location.state.Email
    		this.state.id=props.location.state.id
    	}
    	this.Search=this.Search.bind(this);
    	this.giveValuetosearch=this.giveValuetosearch.bind(this);
  	}
  	giveValuetosearch(e){
  		this.setState({user_search: e.target.value});
  	}
  	Search(){
  		alert(this.state.user_search)
  		axios.post('http://127.0.0.1:8000/users/GetAllInfo/',formData,{headers: {'Content-Type': 'application/json'}})
	    .then(response => {
	         this.setState({email_address:response.data[0]});
	         this.setState({name:response.data[1]});
	         this.setState({surname:response.data[2]});
	         this.setState({Phone_Number:response.data[3]});
	         this.setState({Biography:response.data[4]});
	         this.setState({Professional_Experience:response.data[5]});
	         this.setState({Education:response.data[6]});
	         this.setState({Skills:response.data[7]});

	    }).catch(error => {
	    	alert("Something went wrong")
	    })
  	}
  	render(){
  		if(this.state.con === false ){
  			return (<Redirect to='/'/>);
  		}
  		else{
	  		return(
	  			<div>
	  			<h1>My Network</h1>
	  			<form onSubmit={this.Search}>
			    <label>Search UserEmail:</label>
	  			<input type="text"  id="search_name" name="search_name" defaultValue={this.state.user_search}  onChange={this.giveValuetosearch} size="61"/>
	  			<button class="a">Submit</button>
	  			</form>
	  			</div>
	  		);
	  	}
  	}
}
export default Network;
