import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import Plot from "./Plot.js"
class Personal  extends React.Component {
	constructor(props) {
   		super(props);
   		this.state = {
			access:"Empty",
			Prof_Exp:"",
			Edu:"",
			Skills:"",
			valProf:false,
			valEdu:false,
			valSkills:false,
			id:-1,
			email:"",
	   	};
	   	if(props.location.state == null){
    		this.state.access="Empty"	
    	}
    	else{
    		this.state.access="User"
    	}
    	this.handle_Prof_Exp=this.handle_Prof_Exp.bind(this);
    	this.handle_Edu=this.handle_Edu.bind(this);
    	this.handle_Skills=this.handle_Skills.bind(this);
    	this.handleSubmit=this.handleSubmit.bind(this);
    	this.handle_Prof_Exp=this.handle_Prof_Exp.bind(this);
    	this.handle_Edu=this.handle_Edu.bind(this);
    	this.handle_Skills=this.handle_Skills.bind(this);
  	}
  	handle_Prof_Exp(e){
  		this.setState({Prof_Exp: e.target.value});
  	}
  	handle_Edu(e){
  		this.setState({Edu: e.target.value});
  	}
  	handle_Skills(e){
  		this.setState({Skills: e.target.value});
  	}
  	handle_Prof_Exp(){
  		this.setState({valProf:true});
  	}
  	handle_Edu(){
  		this.setState({valEdu:true});
  	}
  	handle_Skills(){
  		this.setState({valSkills:true});
  	}
  	handleSubmit(){
  		alert(this.state.Prof_Exp)
  		alert(this.state.Edu)
  		alert(this.state.Skills)
  		alert(this.state.valProf)
  		alert(this.state.valProf)
  		alert(this.state.valProf)
  	}
	render(){
		if(this.state.access === "Empty"){
			return (<Redirect to='/'/>);
		}
		else if(this.state.access ==="Show"){

		}
		else{
			return(
				<div>
				<Plot name={"Personal_information"} id={this.state.id} email={this.state.email_address}/>
				<br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<form onSubmit={this.handleSubmit}>
				<label>Professional Experience:</label>
				<textarea id="Professional_Experience" name="Professional_Experience" value={this.state.Prof_Exp}  onChange={this.handle_Prof_Exp} rows="10" cols="100"></textarea>
				<br/>
				<input type="checkbox" id="type1" name="type1" />
				<label for="type1">Private</label><br/>
				<input type="checkbox" id="type2" name="type2" onChange={this.SeenProf}/>
				<label for="type2">Public</label><br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<label>Education:</label>
				<textarea id="Education" name="Education"  value={this.state.Edu}  onChange={this.handle_Edu} rows="10" cols="112"></textarea>
				<br/>
				<input type="checkbox" id="type3" name="type3"/>
				<label for="type3">Private</label><br/>
				<input type="checkbox" id="type4" name="type4" onChange={this.SeenEducation}/>
				<label for="type4">Public</label><br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<label>Skills:</label>
				<textarea id="Skills" name="Skills" value={this.state.Skills}  onChange={this.handle_Skills} rows="10" cols="116"></textarea>
				<br/>
				<input type="checkbox" id="type5" name="type5"/>
				<label for="type5">Private</label><br/>
				<input type="checkbox" id="type6" name="type6" onChange={this.SeenSkills}/>
				<label for="type6">Public</label><br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<br/>
				<button type="submit">Save</button>
				</form>
				</div>
			);
		}
	}
}
export default Personal;
