import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import {useLocation} from "react-router-dom";
import Plot from './Plot.js'
import { BrowserRouter as Router} from 'react-router-dom';
class Result  extends React.Component {
  constructor(props) {
   	super(props);
    this.state = {
      email_address: "",
      id:-1,
      con:false,
      user_search:"",
      check:"No",
      name:"Nothing",
    };
    if(props.location.state == null){
      this.state.con=false  
    }
    else{
      this.state.con=true
      this.state.email_address=props.location.state.Email
      this.state.id=props.location.state.id
      this.state.user_search=props.location.state.user_search
      this.state.val=props.location.state.typesearch
    }
  }
  getData(){
    alert("mpika")
    const formData1=new FormData()
    formData1.append("Email_Address",this.state.email_address)
    axios.post('http://127.0.0.1:8000/users/CheckIfExists/',formData,{headers: {'Content-Type': 'application/json'}})
    .then(response => { 
      this.setState({check:"Yes"});  
    }).catch(error => {
      this.setState({check:"NotExist"});  
      alert("Something went wrong")
    })
    if(this.state.check=="Yes"){
      const formData=new FormData()
      formData.append("user_search",this.state.user_search)
      formData.append("val",this.state.val)
      formData.append("Email_Address",this.state.email_address)
      axios.post('http://127.0.0.1:8000/users/CheckIfFriend/',formData,{headers: {'Content-Type': 'application/json'}})
      .then(response => { 
        this.setState({check:"Yes"});  
      }).catch(error => {
        this.setState({check:"No"});  
       alert("Something went wrong")
      })
    }
    this.setState({name:"Other"});
  }
  render(){
    if(this.state.con === false){
      return(<Redirect to='/'/>);
    }
    else{
      if(this.state.name=="Nothing"){
        this.getData()
      }
      alert("val=",this.state.val)
      if(this.state.check=="NotExist"){
        return(
          <div>
            <h4>Users List</h4>
            <h5>Not Found</h5>
          </div>
        );
      }
      if(this.state.check=="Yes"){
        return(
          <div>
            <h4>Users List</h4>
            <h5>{this.state.email_address}</h5>
          </div>
        );
      }
     }
  }
}
export default Result;
